import React, { useState, useEffect, useCallback } from 'react';
import {
  Container,
  Typography,
  Card,
  CardContent,
  Grid,
  IconButton,
  Box,
  Button,
  Divider,
  Snackbar,
  Alert,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import RemoveIcon from '@mui/icons-material/Remove';
import DeleteIcon from '@mui/icons-material/Delete';
import ShoppingCartCheckoutIcon from '@mui/icons-material/ShoppingCartCheckout';
import axios from 'axios';

function Cart() {
  const [cart, setCart] = useState({ items: [], total: 0 });
  const [openSnackbar, setOpenSnackbar] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState('');
  const [snackbarSeverity, setSnackbarSeverity] = useState('success');
  const [openConfirmDialog, setOpenConfirmDialog] = useState(false);
  const [productToDelete, setProductToDelete] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const showSnackbar = (message, severity = 'success') => {
    setSnackbarMessage(message);
    setSnackbarSeverity(severity);
    setOpenSnackbar(true);
  };

  const fetchCart = useCallback(async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/cart');
      if (response.data && response.data.items) {
        const validItems = response.data.items.filter(item => item && item.product);
        setCart({ ...response.data, items: validItems });
      } else {
        setCart({ items: [], total: 0 });
      }
    } catch (error) {
      console.error('Error fetching cart:', error);
      showSnackbar('Error al cargar el carrito', 'error');
      setCart({ items: [], total: 0 });
    }
  }, []);

  useEffect(() => {
    fetchCart();
  }, [fetchCart]);

  const handleConfirmDelete = (productId) => {
    setProductToDelete(productId);
    setOpenConfirmDialog(true);
  };

  const updateQuantity = async (productId, quantity, stock) => {
    if (quantity < 1 || isLoading) return;
    if (quantity > stock) {
      showSnackbar('No hay suficiente stock disponible', 'error');
      return;
    }

    setIsLoading(true);
    try {
      const response = await axios.put(`http://localhost:5000/api/cart/update/${productId}`, {
        quantity,
      });

      if (response.data) {
        setCart(response.data);
        showSnackbar('Carrito actualizado exitosamente');
      } else {
        showSnackbar('Error al actualizar la cantidad', 'error');
      }
    } catch (error) {
      console.error('Error updating quantity:', error);
      showSnackbar(error.response?.data?.message || 'Error al actualizar la cantidad', 'error');
    } finally {
      setIsLoading(false);
    }
  };

  const removeItem = async () => {
    if (!productToDelete || isLoading) return;

    setIsLoading(true);
    try {
      const response = await axios.delete(`http://localhost:5000/api/cart/remove/${productToDelete}`);
      if (response.data) {
        setCart(response.data);
        showSnackbar('Producto eliminado del carrito');
      } else {
        showSnackbar('Error al eliminar el producto', 'error');
      }
    } catch (error) {
      console.error('Error removing item:', error);
      showSnackbar(error.response?.data?.message || 'Error al eliminar el producto', 'error');
    } finally {
      setIsLoading(false);
      setOpenConfirmDialog(false);
      setProductToDelete(null);
    }
  };

  const handleCheckout = () => {
    showSnackbar('¡Gracias por tu compra!');
  };

  return (
    <Container maxWidth="md" sx={{ py: 8 }}>
      <Typography
        variant="h2"
        align="center"
        color="primary"
        gutterBottom
        sx={{
          fontFamily: 'Playfair Display',
          mb: 6,
          borderBottom: '2px solid',
          borderColor: 'primary.main',
          pb: 2,
        }}
      >
        Carrito de Compras
      </Typography>

      {cart.items.length === 0 ? (
        <Box
          sx={{
            textAlign: 'center',
            py: 8,
          }}
        >
          <Typography variant="h6" color="text.secondary" gutterBottom>
            Tu carrito está vacío
          </Typography>
          <Button
            variant="contained"
            color="primary"
            href="/"
            sx={{
              mt: 2,
              fontFamily: 'Playfair Display',
              textTransform: 'none',
            }}
          >
            Continuar Comprando
          </Button>
        </Box>
      ) : (
        <>
          {cart.items.map((item) => {
            if (!item || !item.product) return null;

            return (
              <Card
                key={item.product._id}
                sx={{
                  mb: 2,
                  backgroundColor: 'background.paper',
                  borderRadius: 2,
                  '&:hover': {
                    boxShadow: 6,
                  },
                }}
              >
                <CardContent>
                  <Grid container alignItems="center" spacing={2}>
                    <Grid item xs={12} sm={3}>
                      <img
                        src={item.product.imageUrl || '/placeholder-image.jpg'}
                        alt={item.product.name || 'Producto'}
                        style={{
                          width: '100%',
                          height: '120px',
                          objectFit: 'cover',
                          borderRadius: '4px',
                        }}
                      />
                    </Grid>
                    <Grid item xs={12} sm={4}>
                      <Typography
                        variant="h6"
                        color="primary"
                        sx={{ fontFamily: 'Playfair Display' }}
                      >
                        {item.product.name || 'Producto'}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        ${typeof item.product.price === 'number' ? item.product.price.toFixed(2) : '0.00'} por unidad
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        Stock disponible: {typeof item.product.stock === 'number' ? item.product.stock : 0}
                      </Typography>
                    </Grid>
                    <Grid item xs={12} sm={3}>
                      <Box
                        sx={{
                          display: 'flex',
                          alignItems: 'center',
                          justifyContent: 'center',
                          gap: 1,
                        }}
                      >
                        <IconButton
                          onClick={() =>
                            updateQuantity(
                              item.product._id,
                              item.quantity - 1,
                              typeof item.product.stock === 'number' ? item.product.stock : 0
                            )
                          }
                          disabled={item.quantity <= 1 || isLoading}
                          color="primary"
                        >
                          <RemoveIcon />
                        </IconButton>
                        <Typography
                          sx={{
                            minWidth: '40px',
                            textAlign: 'center',
                            fontWeight: 'bold',
                          }}
                        >
                          {item.quantity}
                        </Typography>
                        <IconButton
                          onClick={() =>
                            updateQuantity(
                              item.product._id,
                              item.quantity + 1,
                              typeof item.product.stock === 'number' ? item.product.stock : 0
                            )
                          }
                          disabled={
                            item.quantity >= (typeof item.product.stock === 'number' ? item.product.stock : 0) || 
                            isLoading
                          }
                          color="primary"
                        >
                          <AddIcon />
                        </IconButton>
                      </Box>
                    </Grid>
                    <Grid item xs={12} sm={2}>
                      <Box sx={{ textAlign: 'right' }}>
                        <Typography
                          variant="h6"
                          color="primary"
                          sx={{ fontWeight: 'bold' }}
                        >
                          ${(typeof item.product.price === 'number' ? item.product.price * item.quantity : 0).toFixed(2)}
                        </Typography>
                        <IconButton
                          onClick={() => handleConfirmDelete(item.product._id)}
                          color="error"
                          size="small"
                          sx={{ mt: 1 }}
                          disabled={isLoading}
                        >
                          <DeleteIcon />
                        </IconButton>
                      </Box>
                    </Grid>
                  </Grid>
                </CardContent>
              </Card>
            );
          })}

          <Divider sx={{ my: 4 }} />

          <Box sx={{ textAlign: 'right', mb: 4 }}>
            <Typography variant="h4" color="primary" sx={{ fontWeight: 'bold' }}>
              Total: ${cart.total.toFixed(2)}
            </Typography>
          </Box>

          <Box sx={{ textAlign: 'right' }}>
            <Button
              variant="contained"
              color="primary"
              size="large"
              onClick={handleCheckout}
              startIcon={<ShoppingCartCheckoutIcon />}
              disabled={isLoading}
              sx={{
                fontFamily: 'Playfair Display',
                textTransform: 'none',
                py: 1.5,
                px: 4,
              }}
            >
              Finalizar Compra
            </Button>
          </Box>
        </>
      )}

      <Snackbar
        open={openSnackbar}
        autoHideDuration={6000}
        onClose={() => setOpenSnackbar(false)}
      >
        <Alert
          onClose={() => setOpenSnackbar(false)}
          severity={snackbarSeverity}
          sx={{ width: '100%' }}
        >
          {snackbarMessage}
        </Alert>
      </Snackbar>

      <Dialog
        open={openConfirmDialog}
        onClose={() => setOpenConfirmDialog(false)}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">
          {"¿Estás seguro de eliminar este producto del carrito?"}
        </DialogTitle>
        <DialogContent>
          <Typography>
            Esta acción no se puede deshacer.
          </Typography>
        </DialogContent>
        <DialogActions>
          <Button
            onClick={() => setOpenConfirmDialog(false)}
            color="primary"
            disabled={isLoading}
          >
            Cancelar
          </Button>
          <Button
            onClick={removeItem}
            color="error"
            autoFocus
            disabled={isLoading}
          >
            Eliminar
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}

export default Cart;
